startup;load('model.mat')tic;sct_val = apply(nii_list2, GMModel.Sigma, GMModel.mu, GMModel.PComponents);toc;ct = nii1;count = 1;for i=1:192,    for j=1:192,        for k=1:192,            if mask.img(i,j,k) == 255,               ct.img(i,j,k) = sct_val(count, 1);               count = count + 1;            end        end    endendsave_nii(ct, 'sct1.nii')clear i j k count ct