startup;% Load the pre-computed model.load('model.mat');% Get the nii images loadedload_images;% Compute the sCT valuestic;sct_val = get_sct(nii_list, GMModel.Sigma, GMModel.mu, GMModel.PComponents);toc;% Copy the old ct imagect = nii1;% Insert new sCT values in old CT image to preserve the headercount = 1;for i=1:192    for j=1:192        for k=1:192            if mask.img(i,j,k) == 255               ct.img(i,j,k) = sct_val(count, 1);               count = count + 1;            end        end    endend% Save the sCT imagesave_nii(ct, 'sct1.nii')