startup;% Get the nii images loadednii_list = load_patient('010476',...    'Head_CV_UTE_AC_WIP_719 - 13.nii', ...    'Head_CV_UTE_AC_WIP_719 - 14.nii', ...    'Head_CV_UTE_AC_WIP_719_18deg - 18.nii',...    'Head_CV_UTE_AC_WIP_719_18deg - 19.nii',...    'Head_t1_mprage_sag_p2_iso - 7.nii');% Compute the sCT valuestic;sct_val = get_sct(nii_list, model0123.Sigma, model0123.mu, model0123.PComponents);toc;nii1 = load_nii('010476/coreg/ct.nii');% Copy the old ct imagect = nii1;% Insert new sCT values in old CT image to preserve the headercount = 1;for i=1:192    for j=1:192        for k=1:192            if mask.img(i,j,k) == 255               ct.img(i,j,k) = sct_val(count, 1);               count = count + 1;            end        end    endend% Save the sCT imagesave_nii(ct, 'sct1.nii')